import "./Post.css";

const Post = props => {
  return (
    <div className="post">
      {props.post.photo && (
        <img className="post-img" src={props.post.photo} alt="post_img" />
      )}
      <div className="post-info">
        <div className="post-cats">
          {props.post.categories.map(c => (
            <span className="post-cat">c.name</span>
          ))}
        </div>
        <span className="post-title">{props.post.title}</span>
        <hr />
        <span className="post-date">
          {new Date(props.post.createdAt).toDateString()}
        </span>
      </div>
      <p className="post-desc">{props.post.desc}</p>
    </div>
  );
};
export default Post;
