import "./Post.css";

const Post = (props) => {
  return (
    <div className="post">
      <img className="post-img" src="https://images.pexels.com/photos/6945/sunset-summer-golden-hour-paul-filitchkin.jpg?cs=srgb&dl=pexels-snapwire-6945.jpg&fm=jpg" alt="post_img" />
      <div className="post-info" >
      <div className="post-cats" >
      <span className="post-cat">Music</span>
      <span className="post-cat">Life</span>
      </div>
      <span className="post-title">{props.post.title}</span>
      <hr/>
      <span className="post-date">{new Date(props.post.createdAt).toDateString()}</span>
      </div>
      <p className="post-desc">
      {props.post.desc}
      </p>
    </div>
  );
};
export default Post;
