import {useLocation} from 'react-router'
import {useEffect,useState} from 'react'
import "./SinglePost.css";
import axios from 'axios'

const SinglePost = () => {
  const location = useLocation()
  const path=location.pathname.split('/')[2]
const [post,setPost] =useState({})

useEffect(()=>{
  const getPost =async ()=>{
    const res=await axios.get("/posts/"+path)
    setPost(res.data)
  }
  getPost()
},[path])

  return (
    <>
      <div className="singlePost">
        <div className="singlePost-wrapper">
        {post.photo &&
          (<img
            className="singlePost-wrapper-img"
            src="https://images.pexels.com/photos/1539581/pexels-photo-1539581.jpeg?cs=srgb&dl=pexels-tim-mossholder-1539581.jpg&fm=jpg"
            alt="singlePostImg"
          />)}
          <h1 className="singlePost-title">
            {post.title}
            <div className="singlePost-edit">
              <i className="singlePost-icon fas fa-pen-alt" />
              <i className="singlePost-icon fas fa-trash-alt" />
            </div>
          </h1>
          <div className="singlePost-info">
            <span className="singlePost-author">
              Author: <em>{post.username}</em>
            </span>
            <span className="singlePost-date">Date: {new Date(post.createdAt).toDateString()}</span>
          </div>
          <p className="singlePost-desc">
            {post.desc}
          </p>
        </div>
      </div>
    </>
  );
};
export default SinglePost;
