import { useLocation } from "react-router";
import { useEffect, useState,useContext} from "react";
import "./SinglePost.css";
import axios from "axios";
import {Context} from "../../context/Context"
import { Link } from "react-router-dom";

const SinglePost = () => {
  const location = useLocation();
  const path = location.pathname.split("/")[2];
  const [post, setPost] = useState({})
  const {user} = useContext(Context)
  const publicFolder = "http://localhost:3000/images/";


  useEffect(() => {
      const getPost = async () => {
        const res = await axios.get("/posts/" + path);
        setPost(res.data);
      };
      getPost();
    },
    [path]
  );

  return (
    <>
      <div className="singlePost">
        <div className="singlePost-wrapper">
          {post.photo && (
            <img className="singlePost-wrapper-img" src={publicFolder+post.photo} alt="post-img" />
          )}
          <h1 className="singlePost-title">
            {post.title}
            {post.username===user.username && <div className="singlePost-edit">
              <i className="singlePost-icon fas fa-pen-alt" />
              <i className="singlePost-icon fas fa-trash-alt" />
            </div>}

          </h1>
          <div className="singlePost-info">
            <span className="singlePost-author">
              Author:
              <Link to={`/?user=${post.username}`} className="link">
                {" "}
                <em>{post.username}</em>
              </Link>
            </span>
            <span className="singlePost-date">
              Date: {new Date(post.createdAt).toDateString()}
            </span>
          </div>
          <p className="singlePost-desc">{post.desc}</p>
        </div>
      </div>
    </>
  );
};
export default SinglePost;
